import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { Station } from '../station';
import { StationService } from '../station.service';

@Component({
  selector: 'app-reservation-form',
  template: `
  <form>
  <div class="form-group">
    <label for="from">From:</label>
    <select class="form-control" id="from"  name="from">
      <<option *ngFor="let station of stations$" [value]="station._id">{{station.name}}
    </select>
  </div>
  <div class="form-group">
    <label for="to">To:</label>
    <input type="text" class="form-control" id="to"  name="to">
  </div>
  <div class="form-group">
    <label for="date">Date:</label>
    <input type="date" class="form-control" id="date"  name="date">
  </div>
  <div class="form-group">
    <label for="time">Time:</label>
    <input type="time" class="form-control" id="time"  name="time">
  </div>
  <div class="form-group">
  <label for="Train">Train:</label>
  <select class="form-control" id="Train"  name="Train">
    <option>First</option>
    <option>Second</option>
  </select>
</div>
  <div class="form-group">
    <label for="class">Class:</label>
    <select class="form-control" id="class"  name="class">
      <option>First</option>
      <option>Second</option>
    </select>
  </div>
  <button type="submit" class="btn btn-primary">Book</button>
</form>

  `,
  styles: [
  ]
})
export class ReservationFormComponent implements OnInit {
  stations$: Observable<Station[]> = new Observable();

  constructor(private stationService: StationService) { }

  ngOnInit(): void {
    this.fetchStations();
  }



  private fetchStations(): void {
    this.stations$ = this.stationService.getStationss();
  }

}
