import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Observable } from 'rxjs';
import { Station } from '../station';
import { StationService } from '../station.service';


@Component({
  selector: 'app-reservation-form',
  template: `
  <div class="row">
  <form>
  <div class="form-group">
    <label for="from">Destination:</label>
    <select class="form-control" id="from"  name="from">
    <option *ngFor="let station of stations$ | async" [value]="station._id">{{station.name}}</option>
    </select>
  </div>
  <div class="form-group">
    <label for="date">Date:</label>
    <input type="date" class="form-control" id="date"  name="date">
  </div>
  <button type="submit" class="btn btn-primary">Search</button>
</form>
</div>
</>
<div class="row">
<div class="col">
<div class="card">
<img src="https://thumbs.dreamstime.com/b/train-red-railway-station-station-waiting-passengers-36488203.jpg" alt="" class="card-img-top">
<div class="card-body">
  <h5 class="card-title">Udarata Manike</h5>
  <p class="card-text">A001</p>
  <p class="card-text">Price: Rs 10000.00</p>
  <a href="#" class="btn btn-primary" >View Details</a>
</div>
</div>
</div>
<div class="col"></div>
<div class="col"></div>
<div class="col"></div>
</div>

  `,
  styles: [
  ]
})
export class ReservationFormComponent implements OnInit {

  stations$: Observable<Station[]> = new Observable();
 // <option *ngFor="let station of stations$ | async" [value]="station._id">{{station.name}}</option>
  constructor(private stationService: StationService) { }

  ngOnInit(): void {
    this.fetchStations();
  }



  private fetchStations(): void {
    this.stations$ = this.stationService.getStations();
    console.log(this.stations$);
  }

