import { Component, OnInit,Input } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Observable,BehaviorSubject } from 'rxjs';
import { Station } from '../station';
import { StationService } from '../station.service';
import { Train } from '../train';
import { TrainService } from '../train.service';

@Component({
  selector: 'app-reservation-form',
  template: `
  <div class="row">
  <form [formGroup]="searchForm">
  <div class="form-group">
    <label for="from">Destination:</label>
    <select class="form-control" id="from"  name="from">
    <option *ngFor="let station of stations$ | async" [value]="station._id">{{station.name}}</option>
    </select>
  </div>
  <div class="form-group">
    <label for="date">Date:</label>
    <input type="date" class="form-control" id="date"  name="date" formControlName="date">
  </div>
  <br>
  <input type="button" class="btn btn-primary" (click)="viewDetails()" value="Search">
</form>
</div>
<br>
<div class="row">
<div class="col" *ngFor="let train of trains$ | async">
<div class="card">
<img src="{{ train.img_url}}" alt="" class="card-img-top">
<div class="card-body">
  <h5 class="card-title">{{ train.train_name}}</h5>
  <p class="card-text">{{ train.time}}</p>
  <p class="card-text">Price: Rs {{ train.ticket_price}}</p>
  <button class="btn btn-primary me-1" [routerLink]="['reservation/',""train.train_name]">View Details</button>
</div>
</div>
</div>
</div>

  `,
  styles: [
  ]
})
export class ReservationFormComponent implements OnInit {
  @Input()
  initialState: BehaviorSubject<Station> = new BehaviorSubject({});
  private session_id="";
  searchForm: FormGroup = new FormGroup({});
  stations$: Observable<Station[]> = new Observable();
  trains$: Observable<Train[]> = new Observable();

  get date() { return this.searchForm.get('date')!; }
 // <option *ngFor="let station of stations$ | async" [value]="station._id">{{station.name}}</option>
  constructor(private stationService: StationService,private trainService: TrainService,private fb: FormBuilder) { }

  ngOnInit(): void {
    this.fetchStations();
    this.initialState.subscribe(employee => {
      this.searchForm = this.fb.group({
        date: [ ]       
      });
    });

  }



  private fetchStations(): void {
    this.stations$ = this.stationService.getStations();
  }

  viewDetails() {
    console.log(this.searchForm.value);
    this.session_id=this.searchForm.value;
    this.trains$ = this.trainService.getTrains();
  }


}
